import { Header } from '@devvit/runtimes/lib/Header.js';
import fs from 'fs';
import { createRequire } from 'module';
const require = createRequire(import.meta.url);
const packagePath = require.resolve('@devvit/cli/package.json');
const { version } = JSON.parse(fs.readFileSync(packagePath, { encoding: 'utf8' }));
export const HEADER_AUTHORIZATION = (token) => {
    return ['authorization', `bearer ${token}`];
};
export const HEADER_USER_AGENT = () => ['user-agent', 'Devvit/CLI/'.concat(version)];
export const HEADER_DEVVIT_CLI = () => ['x-devvit-cli', 'true'];
export const HEADER_DEVVIT_CANARY = (val) => {
    return [Header.Canary, val];
};
