import fs from 'node:fs';
import path from 'node:path';
import { Permissions } from './permissions.js';
import { TextDecoder } from 'node:util';
import { createRequire } from 'node:module';
const require = createRequire(import.meta.url);
function getPermissionsYamlFilepath() {
    const packageDistPath = path.dirname(require.resolve('@devvit/permissions'));
    return path.join(packageDistPath, 'permissions.yaml');
}
export const defaultPermissions = new Permissions(new TextDecoder().decode(fs.readFileSync(getPermissionsYamlFilepath())));
