export default class AutomodRegex {
    // RPC
    async AutomodRegexMatch(request, _metadata) {
        // eslint-disable-next-line security/detect-non-literal-regexp
        const re = new RegExp(request.pattern);
        return { groups: request.input.match(re) || [] };
    }
    // RPC
    async AutomodRegexTest(request, _metadata) {
        // eslint-disable-next-line security/detect-non-literal-regexp
        const re = new RegExp(request.pattern);
        return { match: re.test(request.input) };
    }
}
