import { Header } from '@devvit/runtimes/lib/Header.js';
import { getFromMetadata } from '@devvit/runtimes/common/envelope/EnvelopeUtil.js';
export function executeSchedulerHandler(schedulerHandler, req, metadata) {
    return schedulerHandler.handler({
        data: req.data,
        context: {
            user: getFromMetadata(Header.User, metadata),
            subreddit: getFromMetadata(Header.Subreddit, metadata),
        },
    }, metadata);
}
