export function makeUniqueIdGenerator() {
    const seenActionIds = new Set();
    return (id) => {
        let uniqueId = id;
        let counter = 1;
        while (seenActionIds.has(uniqueId)) {
            uniqueId = `${id}.${counter++}`;
        }
        seenActionIds.add(uniqueId);
        return uniqueId;
    };
}
