export const Never = {
    create(_runtime) {
        return {
            onDispose() {
                return Promise.resolve();
            },
        };
    },
};
export const WhenZeroed = {
    create(runtime) {
        return {
            async onDispose(uri) {
                if (runtime.refCounts[uri] === 0) {
                    return await runtime.forceUnload(uri);
                }
            },
        };
    },
};
