export function getUniqueNodes(node, getId, getChildren) {
    const seen = new Set([getId(node)]);
    const toProcess = getChildren(node);
    while (toProcess.length > 0) {
        const curNode = toProcess.pop();
        const curId = getId(curNode);
        if (!seen.has(curId)) {
            seen.add(curId);
            toProcess.push(...getChildren(curNode));
        }
    }
    return seen;
}
